def propertiesStep = testCase.getTestStepByName('StoreCsvValues');

rowsData = context.fileReader.readLines();
int rowsize = rowsData.size();


for(int i=0;i<rowsize;i++)
{
	log.info 'Printing row ' +i+ ' values'
    	rowdata = rowsData[i]
    	String[] values = rowdata.split(";")
    	log.info values[0]
    	log.info values[1]

	propertiesStep.setPropertyValue('value1',values[0]);
	propertiesStep.setPropertyValue('value2',values[1]);

	testRunner.runTestStepByName('AddTestRequest');
}

con:configuration.scriptText:
// context - An object that holds information about a validation in-progress, such as the cursor of the content to be validated., etc.
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context );

// request holder
def requestContent = messageExchange.getRequestContentAsXml();

// response holder
def responseContent = messageExchange.getResponseContentAsXml();

// Get input parameters from request xml
def reqholder = groovyUtils.getXmlHolder(requestContent);

// getNodeValue returns string, convert them into Int
def value1 = reqholder.getNodeValue('//tem:intA').toInteger();
def value2 = reqholder.getNodeValue('//tem:intB').toInteger();

log.info 'Input values :' + value1 + ' & ' + value2;

// Get the output parameter from response content
def resholder = groovyUtils.getXmlHolder(responseContent);

// Important: Check response content, there is a namespace defined in 
// response but not used with nodes
resholder.namespaces["ns"] = "http://tempuri.org/"

// Get the response output in Integer 
def addResponseValue = resholder.getNodeValue('//ns:AddResult').toInteger();
log.info 'Response : ' + addResponseValue

// Assert that addition of input values equal to response output
assert (addResponseValue = value1 + value2);
